function findUniqueCharacters($inputString) {\n    $charCount = array();\n\n    // Count occurrences of each character\n    for ($i = 0; $i < strlen($inputString); $i++) {\n        $char = $inputString[$i];\n        if (isset($charCount[$char])) {\n            $charCount[$char]++;\n        } else {\n            $charCount[$char] = 1;\n        }\n    }\n\n    // Collect characters that occur only once\n    $uniqueChars = array();\n    foreach ($charCount as $char => $count) {\n        if ($count == 1) {\n            $uniqueChars[] = $char;\n        }\n    }\n\n    return $uniqueChars;\n}\n\n// Example usage\n$possibleValues = "123456789123456789";\n$uniqueChars = findUniqueCharacters($possibleValues);\nprint_r($uniqueChars);