// Assuming you have the cookie hash stored in a variable\n$user_id = $_COOKIE['user_id'];\n\n// Protect against SQL injection\n$user_id = mysql_real_escape_string($user_id);\n\n// Query to get the songs and join with the favorites table\n$query = "\n    SELECT \n        s.*, \n        UNIX_TIMESTAMP(s.date) AS date,\n        f.USERID,\n        (SELECT COUNT(*) FROM favorites WHERE FAVORITE = s.ID) AS favorite_count\n    FROM \n        songs s\n    LEFT JOIN \n        favorites f ON s.ID = f.FAVORITE AND f.USERID = '$user_id'\n    WHERE \n        s.date >= DATE_SUB(NOW(), INTERVAL 2 WEEK)\n    ORDER BY \n        s.date DESC\n";\n\n$result = mysql_query($query);\n\nif (!$result) {\n    die('Invalid query: ' . mysql_error());\n}\n\n// Fetch and process the results\nwhile ($row = mysql_fetch_assoc($result)) {\n    // Process each row\n    echo "Song ID: " . $row['ID'] . " - Favorites: " . $row['favorite_count'] . "<br>";\n    // Add more code to display your data as needed\n}