<?php\n// Database connection\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Array of IDs\n$idArray = [1, 2, 3, 4, 5];\n\n// Prepare the SQL statement\n$stmt = $conn->prepare("UPDATE your_table SET your_column = ? WHERE id = ?");\n\n// Check if prepare() failed\nif (false === $stmt) {\n    die('prepare() failed: ' . htmlspecialchars($conn->error));\n}\n\n// Loop through the array and execute the prepared statement for each ID\nforeach ($idArray as $id) {\n    // Bind parameters (assuming your_column is being set to some value, e.g., 1)\n    $your_value = 1; // Replace with the actual value you want to set\n    $stmt->bind_param("ii", $your_value, $id);\n\n    // Execute the statement\n    if (!$stmt->execute()) {\n        echo "Execute failed: (" . $stmt->errno . ") " . $stmt->error;\n    }\n}\n\n// Close statement and connection\n$stmt->close();\n$conn->close();\n?>