<?php\nfunction send_knock($host, $port) {\n    $sock = socket_create(AF_INET, SOCK_STREAM, SOL_TCP);\n    if ($sock === false) {\n        echo "Socket creation failed: " . socket_strerror(socket_last_error()) . "\n";\n        return;\n    }\n\n    $result = socket_connect($sock, $host, $port);\n    if ($result === false) {\n        echo "Socket connection failed: " . socket_strerror(socket_last_error($sock)) . "\n";\n        socket_close($sock);\n        return;\n    }\n\n    socket_close($sock);\n    echo "Knocked on port $port\n";\n}\n\n// Example of knocking on three ports\n$host = 'your.server.ip.address';\n$ports = [12345, 23456, 34567];\n\nforeach ($ports as $port) {\n    send_knock($host, $port);\n}\n?>