<?php\n   // Database connection\n   $servername = "localhost";\n   $username = "username";\n   $password = "password";\n   $dbname = "database_name";\n\n   $conn = new mysqli($servername, $username, $password, $dbname);\n\n   if ($conn->connect_error) {\n       die("Connection failed: " . $conn->connect_error);\n   }\n\n   // Get the search term from user input\n   $searchTerm = isset($_GET['search']) ? $_GET['search'] : '';\n\n   // Prevent SQL injection\n   $searchTerm = $conn->real_escape_string($searchTerm);\n\n   // Search query\n   $sql = "SELECT id, quote_text, \n                  (LENGTH(quote_text) - LENGTH(REPLACE(quote_text, '$searchTerm', ''))) / LENGTH('$searchTerm') AS occurrences \n           FROM quotes \n           WHERE quote_text LIKE '%$searchTerm%' \n           ORDER BY occurrences DESC";\n\n   $result = $conn->query($sql);\n\n   // Display results\n   if ($result->num_rows > 0) {\n       while($row = $result->fetch_assoc()) {\n           echo "Quote: " . $row["quote_text"] . "<br>";\n       }\n   } else {\n       echo "No results found.";\n   }\n\n   $conn->close();\n   ?>