<?php\n// Database connection\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Fetch data from the database\n$sql = "SELECT your_column FROM your_table WHERE id = 1"; // Adjust your query as needed\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    $row = $result->fetch_assoc();\n    $data = $row['your_column'];\n    \n    // Explode the data\n    $dataArray = explode(',', $data);\n    \n    // User input for rows and columns\n    $numRows = 3; // Example: 3 rows\n    $numCols = 3; // Example: 3 columns\n\n    // Display the data in table format\n    echo "<table border='1'>";\n    $index = 0;\n    for ($i = 0; $i < $numRows; $i++) {\n        echo "<tr>";\n        for ($j = 0; $j < $numCols; $j++) {\n            if (isset($dataArray[$index])) {\n                echo "<td>___" . $dataArray[$index] . "__</td>";\n            } else {\n                echo "<td>___</td>";\n            }\n            $index++;\n        }\n        echo "</tr>";\n    }\n    echo "</table>";\n} else {\n    echo "No data found";\n}\n\n$conn->close();\n?>