require 'vendor/autoload.php';\n\n    use Elasticsearch\ClientBuilder;\n\n    $client = ClientBuilder::create()->build();\n\n    function indexPost($postId, $postTitle, $postContent) {\n        global $client;\n\n        $params = [\n            'index' => 'forum_posts',\n            'id'    => $postId,\n            'body'  => [\n                'title'   => $postTitle,\n                'content' => $postContent\n            ]\n        ];\n\n        $response = $client->index($params);\n        return $response;\n    }