function searchPosts($queryString) {\n        global $client;\n\n        $params = [\n            'index' => 'forum_posts',\n            'body'  => [\n                'query' => [\n                    'multi_match' => [\n                        'query'  => $queryString,\n                        'fields' => ['title', 'content']\n                    ]\n                ]\n            ]\n        ];\n\n        $response = $client->search($params);\n        return $response['hits']['hits'];\n    }