<?php\nfunction convertToPDF($inputFilePath, $outputFilePath) {\n    $command = 'libreoffice --headless --convert-to pdf --outdir ' . escapeshellarg(dirname($outputFilePath)) . ' ' . escapeshellarg($inputFilePath);\n    exec($command, $output, $return_var);\n\n    if ($return_var != 0) {\n        throw new Exception('Error converting file to PDF: ' . implode("\n", $output));\n    }\n}\n\n// Example usage:\ntry {\n    $inputFilePath = '/path/to/input/document.docx';\n    $outputFilePath = '/path/to/output/document.pdf';\n    convertToPDF($inputFilePath, $outputFilePath);\n    echo 'Conversion successful, PDF located at: ' . $outputFilePath;\n} catch (Exception $e) {\n    echo 'Error: ' . $e->getMessage();\n}\n?>