// Example of a Laravel API Resource\n   namespace App\Http\Resources;\n\n   use Illuminate\Http\Resources\Json\JsonResource;\n\n   class UserResource extends JsonResource\n   {\n       public function toArray($request)\n       {\n           return [\n               'id' => $this->id,\n               'name' => $this->name,\n               'email' => $this->email,\n           ];\n       }\n   }