function get_date_offset($start_date, $end_date, $format = 'm/d/Y') {\n    $start = DateTime::createFromFormat($format, $start_date);\n    $end = DateTime::createFromFormat($format, $end_date);\n    \n    if ($start && $end) {\n        $interval = $start->diff($end);\n        return $interval->days;\n    } else {\n        return false; // Invalid date format\n    }\n}\n\n// Example usage\n$start_date = '10/01/2023';\n$end_date = '10/10/2023';\necho get_date_offset($start_date, $end_date); // Outputs: 9