// Example function to log to MySQL\nfunction logToMySQL($action, $userId, $details) {\n    $conn = new mysqli('hostname', 'username', 'password', 'database');\n    if ($conn->connect_error) {\n        die("Connection failed: " . $conn->connect_error);\n    }\n    \n    $stmt = $conn->prepare("INSERT INTO user_logs (action, user_id, details) VALUES (?, ?, ?)");\n    $stmt->bind_param("sis", $action, $userId, $details);\n    $stmt->execute();\n    $stmt->close();\n    $conn->close();\n}\n\n// Usage\nlogToMySQL('login', 1, 'User logged in');