require_once 'vendor/autoload.php';\n\n$transport = (new Swift_SmtpTransport('smtp.example.com', 587, 'tls'))\n    ->setUsername('your_email@example.com')\n    ->setPassword('your_password');\n\n$mailer = new Swift_Mailer($transport);\n\n$message = (new Swift_Message('Here is the subject'))\n    ->setFrom(['from@example.com' => 'Mailer'])\n    ->setTo(['recipient@example.com' => 'Recipient'])\n    ->setBody('This is the HTML message body <b>in bold!</b>', 'text/html')\n    ->addPart('This is the body in plain text for non-HTML mail clients', 'text/plain')\n    ->attach(Swift_Attachment::fromPath('/path/to/file.txt'));\n\n$result = $mailer->send($message);\n\nif ($result) {\n    echo 'Message has been sent';\n} else {\n    echo 'Message could not be sent';\n}