$user_input = "O'Reilly"; // User input with a special character\n$escaped_input = mysql_real_escape_string($user_input); // Escaped string: O\'Reilly\n\n// SQL query with escaped input\n$query = "INSERT INTO authors (name) VALUES ('$escaped_input')";\nmysql_query($query);\n\n// When you retrieve the data:\n$result = mysql_query("SELECT name FROM authors WHERE name = 'O\'Reilly'");\n$row = mysql_fetch_assoc($result);\necho $row['name']; // Output will be: O'Reilly (without backslashes)