abstract class BaseClass {\n    abstract public function flexibleMethod();\n}\n\nclass ChildClass extends BaseClass {\n    public function flexibleMethod() {\n        $args = func_get_args();\n        // Handle different numbers of parameters here\n        if (count($args) === 1) {\n            // Do something with one parameter\n        } elseif (count($args) === 2) {\n            // Do something with two parameters\n        }\n        // etc.\n    }\n}\n\n$child = new ChildClass();\n$child->flexibleMethod('param1');\n$child->flexibleMethod('param1', 'param2');