function generateShortUniqueId($length = 6) {\n    // Generate random bytes and convert them to a hexadecimal string\n    $bytes = random_bytes(ceil($length / 2));\n    $hex   = bin2hex($bytes);\n\n    // Encode the hexadecimal string in base 36 and return the desired length\n    $base36 = base_convert($hex, 16, 36);\n    return substr($base36, 0, $length);\n}\n\necho generateShortUniqueId(); // Example output: '4d2f8a'