if(isset($_FILES['userfile'])){\n    // Get the original file name\n    $originalFilename = $_FILES['userfile']['name'];\n    \n    // Extract the file extension\n    $fileExtension = pathinfo($originalFilename, PATHINFO_EXTENSION);\n    \n    // Validate the file extension\n    $allowedExtensions = ['jpg', 'jpeg', 'png', 'gif'];\n    if(in_array(strtolower($fileExtension), $allowedExtensions)) {\n        // Assuming you have the user's unique ID in a variable $userID\n        $userID = 'unique_user_id'; // Replace with actual user ID\n        \n        // Create the new file name\n        $newFilename = $userID . '.' . $fileExtension;\n        \n        // Define the path where the file will be saved\n        $uploadDir = 'path/to/upload/directory/'; // Replace with your upload directory\n        $uploadFile = $uploadDir . $newFilename;\n        \n        // Move the uploaded file to the new location\n        if (move_uploaded_file($_FILES['userfile']['tmp_name'], $uploadFile)) {\n            echo "File is valid, and was successfully uploaded.\n";\n        } else {\n            echo "Possible file upload attack!\n";\n        }\n    } else {\n        echo "Invalid file extension.\n";\n    }\n} else {\n    echo "No file uploaded.\n";\n}