use DI\ContainerBuilder;\n\n   // Configure the container\n   $containerBuilder = new ContainerBuilder();\n   $containerBuilder->addDefinitions([\n       ResourceLoader::class => DI\create(DefaultResourceLoader::class),\n       Logger::class => DI\create(DefaultLogger::class),\n   ]);\n   $container = $containerBuilder->build();\n\n   // Define your class\n   class Engine\n   {\n       private $loader;\n       private $logger;\n\n       public function __construct(ResourceLoader $loader, Logger $logger)\n       {\n           $this->loader = $loader;\n           $this->logger = $logger;\n       }\n   }\n\n   // Retrieve an instance from the container\n   $engine = $container->get(Engine::class);