<?php\n\nfunction createDir($dirName) {\n    // Sanitize the directory name to prevent command injection\n    $sanitizedDirName = escapeshellarg($dirName);\n    // Return the command to create the directory\n    return "mkdir -p " . $sanitizedDirName;\n}\n\n$somevariable = 'new_directory_name'; // Set your directory name here\n\n// Get the command to be executed\n$command = createDir($somevariable);\n\n// Check if the command is not empty\nif (!empty($command)) {\n    // Execute the command and capture the output\n    $output = exec($command, $outputLines, $returnVar);\n    \n    // Display the output (if any)\n    echo $output;\n    \n    // Optionally, check if the command was successful\n    if ($returnVar === 0) {\n        echo "Directory created successfully.";\n    } else {\n        echo "Failed to create directory.";\n    }\n} else {\n    echo "Command is empty.";\n}\n?>