class MyClass {\n    public readonly string $name;\n    public readonly int $id;\n\n    public function __construct(string $name, int $id) {\n        $this->name = $name;\n        $this->id = $id;\n    }\n}\n\n$instance = new MyClass("ExampleName", 123);\necho $instance->name; // Outputs: ExampleName\necho $instance->id;   // Outputs: 123\n\n// Attempting to modify the readonly property will result in a fatal error\n// $instance->name = "NewName"; // Fatal error: Cannot modify readonly property MyClass::$name