<?php\n\nnamespace School\Domain;\n\nclass Student\n{\n    private $id;\n    private $name;\n    private $email;\n\n    public function __construct($id, $name, $email)\n    {\n        $this->id = $id;\n        $this->name = $name;\n        $this->email = $email;\n    }\n\n    public function getId()\n    {\n        return $this->id;\n    }\n\n    public function getName()\n    {\n        return $this->name;\n    }\n\n    public function getEmail()\n    {\n        return $this->email;\n    }\n}