class MyClass {\n    private $array = [];\n\n    public function addItem($item) {\n        $this->array[] = $item;\n    }\n\n    public function resetArray() {\n        $this->array = [];\n    }\n\n    public function getArray() {\n        return $this->array;\n    }\n}\n\n$myClass = new MyClass();\n\n// Manually set array to simulate previous state\n$myClass->resetArray();\n$myClass->addItem('item1'); // $this->array[0] = 'item1'\n$myClass->addItem('item2'); // $this->array[1] = 'item2'\n\n// Reset the array\n$myClass->resetArray();\n$myClass->addItem('newItem'); // $this->array[0] = 'newItem'\n\nprint_r($myClass->getArray());