// Set the cookie with SHA1 hash\n$id = 124;\n$salt = 'mysalt';\n$hashedValue = sha1($id . $salt);\nsetcookie('test', $hashedValue, time() + (86400 * 30), "/"); // 86400 = 1 day\n\n// Create a PDO connection\n$dsn = 'mysql:host=your_host;dbname=your_db';\n$username = 'your_username';\n$password = 'your_password';\n$options = [\n    PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,\n    PDO::ATTR_EMULATE_PREPARES => false,\n];\n\ntry {\n    $pdo = new PDO($dsn, $username, $password, $options);\n\n    // Prepare the SQL statement with a placeholder\n    $stmt = $pdo->prepare("SELECT * FROM users WHERE SHA1(CONCAT(id, :salt)) = :hashedValue");\n\n    // Bind the parameters\n    $stmt->bindParam(':salt', $salt);\n    $stmt->bindParam(':hashedValue', $_COOKIE['test']);\n\n    // Execute the query\n    $stmt->execute();\n\n    // Fetch the result\n    $result = $stmt->fetch(PDO::FETCH_ASSOC);\n\n    if ($result) {\n        // Do something with the result\n        print_r($result);\n    } else {\n        echo 'No matching user found.';\n    }\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}