function cleanHtmlText($text) {\n    // Convert to UTF-8 to ensure proper character encoding\n    $text = mb_convert_encoding($text, 'UTF-8', 'UTF-8');\n    \n    // Encode special HTML characters\n    $text = htmlspecialchars($text, ENT_QUOTES | ENT_HTML5, 'UTF-8');\n    \n    // Optionally, you can strip out non-ASCII characters\n    $text = preg_replace('/[^\x20-\x7E]/u', '', $text);\n\n    return $text;\n}\n\n$htmlText = "<p>Some rich text Ã‚ with special characters.</p>";\n$cleanedHtmlText = cleanHtmlText($htmlText);\n\necho $cleanedHtmlText;