<?php\n// Database connection\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$sql = "SELECT \n            DAYOFWEEK(eventDate) AS DAY, \n            YEAR(eventDate) AS YEAR, \n            GROUP_CONCAT(username SEPARATOR ', ') AS usernames,\n            `primary`, \n            secondary, \n            weekend\n        FROM \n            eventcal\n        WHERE \n            region = 4\n            AND MONTH(eventDate) = 7\n        GROUP BY \n            DAYOFWEEK(eventDate), YEAR(eventDate), `primary`, secondary, weekend\n        ORDER BY \n            DAYOFWEEK(eventDate)";\n\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    echo "<table border='1'>\n            <tr>\n                <th>Day</th>\n                <th>Year</th>\n                <th>Usernames</th>\n                <th>Primary</th>\n                <th>Secondary</th>\n                <th>Weekend</th>\n            </tr>";\n    while($row = $result->fetch_assoc()) {\n        echo "<tr>\n                <td>" . $row["DAY"] . "</td>\n                <td>" . $row["YEAR"] . "</td>\n                <td>" . $row["usernames"] . "</td>\n                <td>" . $row["primary"] . "</td>\n                <td>" . $row["secondary"] . "</td>\n                <td>" . $row["weekend"] . "</td>\n              </tr>";\n    }\n    echo "</table>";\n} else {\n    echo "0 results";\n}\n$conn->close();\n?>