<?php\n// List of allowed referrer URLs\n$allowed_referrers = array(\n    'https://example.com',\n    'https://another-allowed-site.com'\n);\n\n// Get the referrer\n$referrer = isset($_SERVER['HTTP_REFERER']) ? $_SERVER['HTTP_REFERER'] : '';\n\n// Check if the referrer is in the list of allowed referrers\n$is_allowed = false;\nforeach ($allowed_referrers as $allowed_referrer) {\n    if (strpos($referrer, $allowed_referrer) === 0) {\n        $is_allowed = true;\n        break;\n    }\n}\n\nif ($is_allowed) {\n    echo "Access granted.";\n    // Proceed with your application logic\n} else {\n    echo "Access denied.";\n    // Optionally, you can redirect the user or show an error message\n    header('HTTP/1.0 403 Forbidden');\n    exit("You are not allowed to access this application from your current referrer.");\n}\n?>