<?php\n$countries = array(\n    "217" => "Turkmenistan",\n    "218" => "Turks and Caicos Islands",\n    "219" => "Tuvalu",\n    "220" => "Uganda",\n    "221" => "Ukraine",\n    "222" => "United Arab Emirates",\n    "223" => "United Kingdom (Great Britain)",\n    "224" => "United States"\n);\n\n$mycountry = 224;\n\n// Reorder countries with USA and UK at the top\n$orderedCountries = array(\n    "224" => "United States",\n    "223" => "United Kingdom (Great Britain)"\n);\n\nforeach ($countries as $id => $name) {\n    if ($id != 224 && $id != 223) {\n        $orderedCountries[$id] = $name;\n    }\n}\n?>\n<select name="country" style="width:180px;" onChange="do_get_rest_popup(this.value)">\n<?php\nforeach ($orderedCountries as $id => $name) {\n    $selected = ($id == $mycountry) ? 'selected' : '';\n    echo "<option value=\"$id\" $selected>$name</option>";\n}\n?>\n</select>