<?php\nrequire 'vendor/autoload.php';\n\nuse Cron\CronExpression;\n\n// Define your cron expressions\n$cronExpressions = [\n    '1 * * * *',       // every hour at xx:01\n    '2 5 * * *',       // every day at 05:02\n    '0 4 3 * *',       // every third day of the month at 04:00\n    '* 2 * * 5',       // every minute between 02:00 and 02:59 on Fridays\n];\n\n// Define your Unix timestamp\n$timestamp = 1672531200; // Example timestamp\n\n// Convert timestamp to DateTime object\n$date = new DateTime("@$timestamp");\n\n// Find the next execution time for each cron expression\nforeach ($cronExpressions as $cronExpression) {\n    $cron = CronExpression::factory($cronExpression);\n    $nextRun = $cron->getNextRunDate($date);\n    echo "Next run for '$cronExpression' is at " . $nextRun->format('Y-m-d H:i:s') . "\n";\n}\n?>