function get_ucs2_codepoint($char) {\n    $utf16 = mb_convert_encoding($char, 'UTF-16', 'UTF-8');\n    $ucs2 = unpack('n', $utf16);\n    return sprintf('%04X', $ucs2[1]);\n}\n\nfunction utf8_to_ucs2($string) {\n    $result = [];\n    $length = mb_strlen($string, 'UTF-8');\n    for ($i = 0; $i < $length; $i++) {\n        $char = mb_substr($string, $i, 1, 'UTF-8');\n        $result[] = get_ucs2_codepoint($char);\n    }\n    return implode(' ', $result);\n}\n\n// Example usage\n$input = "hello";\n$output = utf8_to_ucs2($input);\necho $output; // Output: 0068 0065 006C 006C 006F