<?php\n// Start time measurement\n$start_time = microtime(true);\n\n// Connect to MySQL database\n$mysqli = new mysqli("your_database_host", "your_database_username", "your_database_password", "your_database_name");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Execute a query (Replace with your actual query)\n$query = "SELECT * FROM your_table LIMIT 1";\n$result = $mysqli->query($query);\n\n// Fetch result (if needed)\nif ($result->num_rows > 0) {\n    while ($row = $result->fetch_assoc()) {\n        // Process each row (if needed)\n    }\n}\n\n// Close the connection\n$mysqli->close();\n\n// End time measurement\n$end_time = microtime(true);\n\n// Calculate the total execution time\n$execution_time = ($end_time - $start_time);\n\n// Display the result\necho "Total Execution Time: " . $execution_time . " seconds";\n?>