<?php\nrequire_once __DIR__ . '/vendor/autoload.php'; // Change path as needed\n\nuse Facebook\Facebook;\n\nfunction getLatestFacebookStatus($accessToken, $pageId) {\n    $fb = new Facebook([\n        'app_id' => 'your-app-id',\n        'app_secret' => 'your-app-secret',\n        'default_graph_version' => 'v11.0',\n    ]);\n\n    try {\n        // Returns a `Facebook\FacebookResponse` object\n        $response = $fb->get("/$pageId/posts?fields=message,created_time&limit=1", $accessToken);\n        $posts = $response->getDecodedBody();\n        if (!empty($posts['data'])) {\n            return $posts['data'][0];\n        } else {\n            return null; // No posts found\n        }\n    } catch(Facebook\Exceptions\FacebookResponseException $e) {\n        echo 'Graph returned an error: ' . $e->getMessage();\n        exit;\n    } catch(Facebook\Exceptions\FacebookSDKException $e) {\n        echo 'Facebook SDK returned an error: ' . $e->getMessage();\n        exit;\n    }\n}\n\n// Usage\n$accessToken = 'your-access-token';\n$pageId = 'your-page-id';\n$status = getLatestFacebookStatus($accessToken, $pageId);\n\nif ($status) {\n    echo 'Latest Status: ' . $status['message'] . ' (Posted on: ' . $status['created_time'] . ')';\n} else {\n    echo 'No status updates found.';\n}\n?>