// Improved code using prepared statements\n$date = time();\n$top_level_category = $_POST['top_level_category'];\n$sub_level_category = $_POST['sub_level_category'];\n$company = $_POST['company'];\n$agent_name = $_POST['agent_name'];\n$ticket_id = $_POST['ticket_id'];\n\n$mysqli = new mysqli("host", "user", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$stmt = $mysqli->prepare("INSERT INTO dacc_data (top_level_category, sub_level_category, agent_name, date, ticket_id, company) VALUES (?, ?, ?, FROM_UNIXTIME(?), ?, ?)");\n$stmt->bind_param("ssssis", $top_level_category, $sub_level_category, $agent_name, $date, $ticket_id, $company);\n\nif ($stmt->execute()) {\n    echo "New record created successfully";\n} else {\n    echo "Error: " . $stmt->error;\n}\n\n$stmt->close();\n$mysqli->close();