<?php\nfunction outer($msg) {\n    function inner($msg) {\n        echo 'inner: ' . $msg . ' ';\n    }\n    echo 'outer: ' . $msg . ' ';\n    inner($msg);\n}\n\n// This will cause a Fatal error because inner() is not defined in the global scope\n// inner('test1');  \n\nouter('test2');  // Output: outer: test2 inner: test2\n\n// Now inner() is defined in the global scope because outer() has been called\ninner('test3');  // Output: inner: test3\n\n// This will cause a Fatal error because inner() cannot be redeclared\n// outer('test4');  \n?>