<?php\n// Database connection\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// SQL query to get the top 5 ratings\n$sql = "SELECT id, mid, uid, rating FROM your_table ORDER BY rating DESC LIMIT 5";\n$result = $conn->query($sql);\n\n// Check if results exist and output data\nif ($result->num_rows > 0) {\n    // Output data of each row\n    while($row = $result->fetch_assoc()) {\n        echo "id: " . $row["id"]. " - mid: " . $row["mid"]. " - uid: " . $row["uid"]. " - Rating: " . $row["rating"]. "<br>";\n    }\n} else {\n    echo "0 results";\n}\n\n// Close connection\n$conn->close();\n?>