<?php\n// Example input\n$tagsString = $_GET['tags'];\n\n// Split the string by commas into an array\n$tagsArray = explode(',', $tagsString);\n\n// Trim each item and apply security functions\n$tags = array_map(function($tag) {\n    $trimmedTag = trim($tag);\n    $secureTag = htmlspecialchars(strip_tags($trimmedTag), ENT_QUOTES, 'UTF-8');\n    return $secureTag;\n}, $tagsArray);\n\n// Example output\nprint_r($tags);\n?>