<?php\n$htaccessContent = "SetEnv DOCUMENT_ROOT " . $_SERVER['DOCUMENT_ROOT'] . "\n";\n$htaccessContent .= "<IfModule mod_rewrite.c>\n";\n$htaccessContent .= "    RewriteEngine On\n";\n$htaccessContent .= "    RewriteRule ^somepath/(.*)$ \${DOCUMENT_ROOT}/somepath/\$1 [L]\n";\n$htaccessContent .= "</IfModule>\n";\n\nfile_put_contents($_SERVER['DOCUMENT_ROOT'] . '/.htaccess', $htaccessContent);\n?>