<?php\nsession_start();\n\nfunction scriptNext($script_id) {\n    if (empty($script_id)) {\n        trigger_error("script id is not defined", E_USER_ERROR);\n    }\n\n    $page_stack = $_SESSION['page_stack'] ?? [];\n    \n    if (in_array($script_id, $page_stack)) {\n        do {\n            $last = array_pop($page_stack);\n        } while ($last != $script_id);\n    }\n\n    $page_stack[] = $script_id;\n    $_SESSION['page_stack'] = $page_stack;\n\n    $location = 'http://' . $_SERVER['HTTP_HOST'] . $script_id;\n    header('Location: ' . $location);\n    exit;\n}\n\nfunction scriptPrevious() {\n    $page_stack = $_SESSION['page_stack'] ?? [];\n    array_pop($page_stack);\n    $_SESSION['page_stack'] = $page_stack;\n\n    if (!empty($page_stack)) {\n        $last_script = end($page_stack);\n        $location = 'http://' . $_SERVER['HTTP_HOST'] . $last_script;\n        header('Location: ' . $location);\n        exit;\n    } else {\n        // Handle case when no previous script is available\n        echo "No previous script available.";\n    }\n}\n\nfunction scriptCurrent() {\n    $page_stack = $_SESSION['page_stack'] ?? [];\n    if (!empty($page_stack)) {\n        $current_script = end($page_stack);\n        return $current_script;\n    } else {\n        return null;\n    }\n}\n\n// Example usage\n// scriptNext('/path/to/new/script.php');\n// scriptPrevious();\n// $current_script = scriptCurrent();