<?php\n\nfunction hashFile($filePath) {\n    // Calculate the hash of the file using MD5 or SHA-256\n    return hash_file('sha256', $filePath); // Using SHA-256 for better security\n}\n\nfunction isDuplicateFile($uploadedFilePath, $existingFilesDirectory) {\n    $uploadedFileHash = hashFile($uploadedFilePath);\n\n    // Scan the directory for existing files\n    $existingFiles = scandir($existingFilesDirectory);\n    foreach ($existingFiles as $file) {\n        if ($file !== '.' && $file !== '..') {\n            $existingFilePath = $existingFilesDirectory . DIRECTORY_SEPARATOR . $file;\n            if (is_file($existingFilePath)) {\n                $existingFileHash = hashFile($existingFilePath);\n                if ($uploadedFileHash === $existingFileHash) {\n                    return true; // Duplicate file found\n                }\n            }\n        }\n    }\n    return false; // No duplicates found\n}\n\n// Example usage\n$uploadedFilePath = '/path/to/uploaded/file';\n$existingFilesDirectory = '/path/to/existing/files';\n\nif (isDuplicateFile($uploadedFilePath, $existingFilesDirectory)) {\n    echo "Duplicate file detected.";\n} else {\n    echo "No duplicate file detected.";\n}\n?>