<?php\n$page = isset($_GET['page']) ? (int)$_GET['page'] : 1;\n$limit = 10;  // Number of rows per page\n$offset = ($page - 1) * $limit;\n\n// Example of the original complex SQL query\n$originalQuery = "SELECT a, b, c FROM your_table WHERE some_condition = true GROUP BY a, b, c ORDER BY a";\n\n// Step 1: Get the total count of rows\n$countQuery = "SELECT COUNT(*) as total_rows FROM ("\n    . "SELECT 1 FROM your_table WHERE some_condition = true GROUP BY a, b, c"\n    . ") as subquery";\n$totalRowsResult = $pdo->query($countQuery);\n$totalRows = $totalRowsResult->fetch(PDO::FETCH_ASSOC)['total_rows'];\n$totalPages = ceil($totalRows / $limit);\n\n// Step 2: Get the paged results\n$pagedQuery = $originalQuery . " LIMIT :limit OFFSET :offset";\n$stmt = $pdo->prepare($pagedQuery);\n$stmt->bindValue(':limit', $limit, PDO::PARAM_INT);\n$stmt->bindValue(':offset', $offset, PDO::PARAM_INT);\n$stmt->execute();\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n// Display or process the results\nforeach ($results as $row) {\n    // Your code to display each row\n}\n\n// Generate pagination links\nfor ($i = 1; $i <= $totalPages; $i++) {\n    echo "<a href=\"?page=$i\">Page $i</a> ";\n}\n?>