function fireAndForget($url) {\n    $url_info = parse_url($url);\n    $host = $url_info['host'];\n    $port = isset($url_info['port']) ? $url_info['port'] : 80;\n    $path = isset($url_info['path']) ? $url_info['path'] : '/';\n    $query = isset($url_info['query']) ? '?' . $url_info['query'] : '';\n\n    $fp = fsockopen($host, $port, $errno, $errstr, 30);\n    if ($fp) {\n        $out = "GET " . $path . $query . " HTTP/1.1\r\n";\n        $out .= "Host: " . $host . "\r\n";\n        $out .= "Connection: Close\r\n\r\n";\n        fwrite($fp, $out);\n        fclose($fp);\n    }\n}\n\n// Example usage\nfireAndForget('http://internal-IP/userTracker.php?Name=UserName&Page=...');\n\n// Continue with the rest of your code\n// line 1  normal code\n// line 2  normal code\n// line 4  Code that proceeds immediately after line 3 without waiting for a reply of any sort.