function isReallyEmpty($var) {\n    if (is_null($var)) {\n        return true;\n    }\n    if (is_string($var) && trim($var) === '') {\n        return true;\n    }\n    if (is_array($var) && empty($var)) {\n        return true;\n    }\n    if (is_object($var) && empty((array)$var)) {\n        return true;\n    }\n    return false;\n}\n\n// Usage examples\nvar_dump(isReallyEmpty(null));        // true\nvar_dump(isReallyEmpty(""));          // true\nvar_dump(isReallyEmpty([]));          // true\nvar_dump(isReallyEmpty(new stdClass));// true\n\nvar_dump(isReallyEmpty(0));           // false\nvar_dump(isReallyEmpty("0"));         // false\nvar_dump(isReallyEmpty(123));         // false\nvar_dump(isReallyEmpty("some text")); // false\nvar_dump(isReallyEmpty([1, 2, 3]));   // false