// In your custom view helper directory (e.g., application/views/helpers/UrlWithAnchor.php)\nclass Zend_View_Helper_UrlWithAnchor extends Zend_View_Helper_Abstract\n{\n    public function urlWithAnchor($urlOptions = array(), $name = null, $reset = false, $encode = true, $anchor = '')\n    {\n        $url = $this->view->url($urlOptions, $name, $reset, $encode);\n        return $url . '#' . ltrim($anchor, '#');\n    }\n}