<?php\n$author_array = [8, 1, 6];\n\n$user_results = [\n    (object) ['ID' => 1, 'user_login' => 'user1'],\n    (object) ['ID' => 6, 'user_login' => 'user6'],\n    (object) ['ID' => 8, 'user_login' => 'user8'],\n];\n\nusort($user_results, function($a, $b) use ($author_array) {\n    $pos_a = array_search($a->ID, $author_array);\n    $pos_b = array_search($b->ID, $author_array);\n    return $pos_a - $pos_b;\n});\n\nprint_r($user_results);\n?>