// Assume this is part of msn.clas.php\n\n// Function to auto-accept friend requests\nfunction autoAcceptRequests($msn) {\n    while ($msn->newFriendRequests()) {\n        $request = $msn->getNextFriendRequest();\n        $msn->acceptFriendRequest($request);\n        echo "Accepted friend request from: " . $request->email . "\n";\n    }\n}\n\n// Usage\ninclude('path/to/msn.clas.php');\n\n$msn = new MSNClass();\n$msn->connect($username, $password);\n\n// Call the auto-accept function\nautoAcceptRequests($msn);\n\n// Continue with other operations, e.g., sending messages\n// ...\n\n$msn->disconnect();