class ClassA {\n    public function performAction(ClassB $b) {\n        // ClassA calls a protected method in ClassB\n        $b->internalMethod($this);\n    }\n}\n\nclass ClassB {\n    protected function internalMethod(ClassA $a) {\n        // Perform actions using $a\n        echo "Internal method called by " . get_class($a);\n    }\n}\n\n// Usage\n$a = new ClassA();\n$b = new ClassB();\n$a->performAction($b);  // This works\n\n// $b->internalMethod($a);  // This will cause an error because the method is protected