<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    $selected_option = $_POST['option'];\n    $other_option = !empty($_POST['other_option']) ? $_POST['other_option'] : null;\n\n    if ($selected_option == 'Other' && $other_option) {\n        $final_selection = $other_option;\n    } else {\n        $final_selection = $selected_option;\n    }\n\n    // Process the final selection\n    echo "You selected: " . htmlspecialchars($final_selection);\n}\n?>