class user {\n    var $password;\n    var $db;\n    var $user_id;\n\n    function __construct($id) {\n        global $DB;\n        $this->db = new db($DB['host'], $DB['user'], $DB['pass'], $DB['database']);\n        $this->user_id = $id;\n        $u_result = $this->db->run("SELECT * FROM users WHERE use_id = " . $this->db->escape($this->user_id));\n\n        if ($u_result) {\n            $this->password = trim($u_result[0]['password']);\n        } else {\n            // Handle the error, e.g., user not found\n        }\n    }\n\n    function match_password($password) {\n        return (trim($this->password) === md5($password));\n    }\n}\n\n// Debugging\n$user = new user($some_user_id);\n$entered_password_hash = md5($_POST['pass']);\necho "Entered Password Hash: " . $entered_password_hash . "<br>";\necho "Stored Password Hash: " . $user->password . "<br>";\n\nif ($user->match_password($_POST['pass']) == true) {\n    echo "Password match!";\n    // Proceed with login\n} else {\n    echo "Password does not match!";\n    // Handle the error\n}