$newIncludePath = array();\n$newIncludePath[] = '../ZendGdata-1.8.4PL1/library';\n$newIncludePath = implode($newIncludePath);\nset_include_path($newIncludePath);\n\n// Load classes\nrequire_once 'Zend/Loader.php';\nZend_Loader::loadClass('Zend_Gdata');\nZend_Loader::loadClass('Zend_Gdata_ClientLogin');\nZend_Loader::loadClass('Zend_Gdata_Calendar');\nZend_Loader::loadClass('Zend_Http_Client');\n\n// Connect to service\n$gcal = Zend_Gdata_Calendar::AUTH_SERVICE_NAME;\n$user = "********@gmail.com";\n$pass = "*****";\n$client = Zend_Gdata_ClientLogin::getHttpClient($user, $pass, $gcal);\n$gcal = new Zend_Gdata_Calendar($client);\n\n// Construct event object and save to server\ntry {\n    $event = $gcal->newEventEntry();\n    $event->title = $gcal->newTitle($title);\n    $event->content = $gcal->newContent($desc);\n    $when = $gcal->newWhen();\n    $when->startTime = $date;\n    $when->endTime = $date;\n    $event->when = array($when);\n    \n    $createdEvent = $gcal->insertEvent($event);\n    \n    // Get the event ID or URL\n    $eventId = $createdEvent->id->text;\n    $editLink = $createdEvent->getEditLink()->href;\n    \n    echo "Event ID: " . $eventId . "<br>";\n    echo "Edit Link: " . $editLink . "<br>";\n    \n} catch (Zend_Gdata_App_Exception $e) {\n    echo "Error: Unable to add event to Google Calendar - " . $e->getMessage();\n}