<?php\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n$searchTerms = "apple iphone applications";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Split the search terms into an array\n$searchTermsArray = explode(' ', $searchTerms);\n$searchString = implode(' ', $searchTermsArray);\n\n// Perform the full-text search query\n$sql = "SELECT title, content, \n        MATCH(title, content) AGAINST(?) AS relevancy \n        FROM articles \n        WHERE MATCH(title, content) AGAINST(?) \n        ORDER BY relevancy DESC";\n\n$stmt = $conn->prepare($sql);\n$stmt->bind_param('ss', $searchString, $searchString);\n$stmt->execute();\n$result = $stmt->get_result();\n\nif ($result->num_rows > 0) {\n    // Output data of each row\n    while($row = $result->fetch_assoc()) {\n        echo "Title: " . $row["title"]. " - Content: " . $row["content"]. " - Relevancy: " . $row["relevancy"]. "<br>";\n    }\n} else {\n    echo "0 results";\n}\n$conn->close();\n?>