<?php\nsession_start();\n// Assuming user is logged in and $username is their username\n$username = $_SESSION['username'];\n$token = bin2hex(random_bytes(32));\n$_SESSION['token'] = $token;\n\n// Store $token and $username securely, e.g., in a database or cache\n// Also set an expiration time for the token\n\n// Generate a link to Mantis with the token\n$mantis_url = "https://mantis.example.com/auth.php?user=$username&token=$token";\n?>\n\n<a href="<?php echo $mantis_url; ?>">Report a Bug</a>